import React, { Component } from 'react';
import './App.css';
import Cards from './Cards';

class App extends Component {

  peo = () => {
    return(
      fetch("https://swapi.co/api/people")
        .then(result => result.json())
        .then(data => data.results)      
    );    
  }

  constructor(){
    super()
    this.state = {
      people1: this.peo(),
      people2: []
    }
  }

  render(){
    return(
      <div>
        <Cards people = { this.state.people2 } />               
      </div>
    )
  }

  componentDidMount(){
    return(
      fetch("https://swapi.co/api/people")
        .then(result => result.json())
        .then(data => this.setState({ people2: this.state.people1 }))      
    );
  }   
}

export default App;
