import React from 'react';

const DropDown = ({ people }) => {
	return(
		<div>
			<select>
			{people && 
				people.map((person, i) => {
					return(
						<option>{ people[i].name }</option>  	
					)	
				})			
			}
			</select>
		</div>
	)
}

export default DropDown;
