import React, { Component } from 'react';
import './App.css';
import CardsList from './CardsList';
import DropDown from './DropDown';

class App extends Component {

  constructor(){
    super()
    this.state = {
      people1: "",
      people2: ""
    }
  }

  clickDropDown = (event) => {
    if(event.target.value !== "Select your character"){
      this.setState({ people2: this.state.people1.filter(arr => arr.name === event.target.value) })
      this.setState({ people1: this.state.people2 })
    }
  }

  render(){
    return(
      <div>
        <DropDown people = {this.state.people1} cldd = {this.clickDropDown}/>
        <CardsList people = {this.state.people1}/>
      </div>
    )
  }

  async componentDidMount(){              
    fetch("https://swapi.co/api/people")
      .then(result => result.json())
      .then(data => this.setState({people1: data.results}))                            
  }
}

export default App;
